mapper.insert:
INSERT INTO USER(user_id
								, password
								, user_name
								, address
								, phone
								, email
								, last_date
								, role
								)
			VALUES (#{userId}
								, #{password}
								, #{userName}
								, #{address}
								, #{phone}
								, #{email}
								, NOW()
								, 'USER'
								)

mapper.select:
SELECT user_id
FROM USER
WHERE user_id = #{userId}

SELECT email
FROM USER
WHERE email = #{email}
