mapper.insert:
INSERT INTO USER(user_id
								, user_pw
								, user_name
								, user_address
								, user_phone
								, user_email
								, user_lastlogin
								, user_authority
								)
			VALUES (#{userId}
								, #{userPw}
								, #{userName}
								, #{userAddress}
								, #{userPhone}
								, #{userEmail}
								, NOW()
								, 'USER'
								)

mapper.select:
SELECT *
FROM USER
WHERE user_id = #{userId}
AND user_pw = #{userPw}
